:root {
  /* Primary Brand */
  --ch-primary-500: #3056F5;
  --ch-primary-600: #2B3CF5;

  /* Text */
  --ch-text-primary: #0F172A;
  --ch-text-secondary: #475569;
  --ch-text-tertiary: #64748B;
  --ch-text-muted: #94A3B8;
  --ch-text-placeholder: #CBD5E1;

  /* Background */
  --ch-bg-page: #FFFFFF;
  --ch-bg-surface: #FFFFFF;
  --ch-bg-subtle: #F9FAFB;

  /* Border */
  --ch-border-default: #E5E7EB;
  --ch-border-subtle: #F1F5F9;
  --ch-border-hover: #D1D5DB;

  /* Radius */
  --ch-radius-sm: 0.5rem;
  --ch-radius-md: 0.75rem;
  --ch-radius-lg: 1rem;
  --ch-radius-xl: 1.25rem;
  --ch-radius-2xl: 1.5rem;

  /* Shadow */
  --ch-shadow-sm: 0 1px 3px 0 rgba(15, 23, 42, 0.04);
  --ch-shadow-md: 0 4px 6px -1px rgba(15, 23, 42, 0.06);
  --ch-shadow-lg: 0 10px 15px -3px rgba(15, 23, 42, 0.08);
  --ch-shadow-xl: 0 20px 25px -5px rgba(15, 23, 42, 0.09);

  /* Transition */
  --ch-transition-fast: 150ms;
  --ch-transition-base: 250ms;
  --ch-transition-slow: 350ms;
  --ch-ease-smooth: cubic-bezier(0.4, 0, 0.2, 1);
}

