implementation_hints.react_button_example:
import { cn } from '@/lib/utils';

export function Button({ 
  variant = 'primary', 
  size = 'default', 
  className, 
  children, 
  ...props 
}) {
  const baseStyles = '
    inline-flex items-center justify-center
    font-semibold rounded-lg
    transition-all duration-250 ease-smooth
    focus-visible:outline-none focus-visible:ring-3 focus-visible:ring-primary/20
    disabled:opacity-50 disabled:cursor-not-allowed
  ';

  const variants = {
    primary: '
      bg-[#3056F5] text-white
      hover:bg-[#2B3CF5] hover:-translate-y-0.5 hover:shadow-[0_8px_24px_rgba(48,86,245,0.32)]
      active:translate-y-0 active:scale-98
      shadow-[0_4px_12px_rgba(48,86,245,0.24)]
    ',
    secondary: '
      bg-gray-100 text-gray-700 border border-gray-200
      hover:bg-gray-200 hover:border-gray-300
    ',
    ghost: '
      text-gray-600
      hover:bg-primary/8 hover:text-[#3056F5]
    '
  };

  const sizes = {
    sm: 'h-9 px-4 text-sm',
    default: 'h-11 px-6 text-[15px]',
    lg: 'h-13 px-8 text-base'
  };

  return (
    <button
      className={cn(baseStyles, variants[variant], sizes[size], className)}
      {...props}
    >
      {children}
    </button>
  );
}

implementation_hints.premium_card_css:
.premium-card {
  position: relative;
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.95), rgba(249, 250, 251, 0.95));
  border: 1px solid rgba(48, 86, 245, 0.1);
  border-radius: 1.25rem;
  padding: 1.5rem;
  box-shadow: 0 8px 24px -4px rgba(48, 86, 245, 0.12);
  transition: all 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

.premium-card:hover {
  transform: translateY(-6px) scale(1.01);
  box-shadow: 0 20px 40px -8px rgba(15, 23, 42, 0.12);
  border-color: rgba(48, 86, 245, 0.2);
}

.premium-card::before {
  content: '';
  position: absolute;
  inset: -1px;
  border-radius: inherit;
  padding: 1px;
  background: linear-gradient(135deg, rgba(48, 86, 245, 0.2), rgba(34, 197, 94, 0.1));
  mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
  mask-composite: exclude;
  pointer-events: none;
  opacity: 0;
  transition: opacity 250ms;
}

.premium-card:hover::before {
  opacity: 1;
}

